import React, { Component } from 'react';
import PhoneValidationService from '../service/PhoneValidationService';
import TextInput from './TextInput';
import Select from './Select';

class ValidationApp extends Component {
    constructor(props) {
        super(props)

        this.state = {
            formControls: {
                phone: {
                    value: '',
                    placeholder: 'input phone'
                },
                phoneHistory: {
                    value: '',
                    options: [
                        { value: '90001000', displayValue: '90001000'},
                        { value: '90002000', displayValue: '90002000'}
                    ]
                }
            },
            message: null
        }


        this.onSubmit = this.onSubmit.bind(this)
    }

    changeHandler = event => {
        const name = event.target.name;
        const value = event.target.value;

        const updatedControls={
            ...this.state.formControls
        };
        const updatedFormElement={
            ...updatedControls[name]
        };
        updatedFormElement.value = value;

        updatedControls[name] = updatedFormElement;

        this.setState({
            formControls: updatedControls
        }

        );

        console.log(`name: ${name}, value: ${value}`);
    }

    selectHandler = event => {
        console.log('selectHandler');

        let selected = '';

        const updatedControls={
            ...this.state.formControls
        };

        updatedControls['phone'].value = updatedControls['phoneHistory'].value;

        this.setState({
            formControls: updatedControls
        }

        );

        console.log(`name: ${name}, value: ${value}`);
    }

    onSubmit = () => {
        const formData = {};
        for (let formElementId in this.state.formControls) {
            formData[formElementId] = this.state.formControls[formElementId].value;
        }
        console.dir(formData);
    };


    render() {
        console.log('render')
        return (
            <div className="container">
                <h3>ValidationFormApp</h3>
                {this.state.message && <div className="alert alert-success">{this.state.message}</div>}
                <TextInput name="phone"
                            value={this.state.formControls.phone.value}
                            onChange={this.changeHandler}
                            placeholder={this.state.formControls.phone.placeholder}
                />

                <Select name="phoneHistory"
                            value={this.state.formControls.phoneHistory.value}
                            onChange={this.changeHandler}
                            options={this.state.formControls.phoneHistory.options}
                />

                <button onClick={this.onSubmit}>
                    validate
                </button>

            </div>
        )
    }
}

export default ValidationApp
