import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import PhoneValidationService from '../service/PhoneValidationService';

class ValidationFormApp extends Component {
    constructor(props) {
        super(props)

        this.state = {
            phoneHistory: [],
            message: null
        }
        this.onSubmit = this.onSubmit.bind(this)
        this.onHistoryChange = this.onHistoryChange.bind(this)
    }

    onSubmit(values) {
        const phoneValue = values.phone


        PhoneValidationService.validate(phoneValue)
            .then( rtn =>  {
                console.log(rtn)
                this.setState( state => {
                    const history = state.phoneHistory.concat(phoneValue);
                    return {
                        phoneHistory : history,
                        message : rtn.data.message
                    };
                    })

                }
            )
    }

    onHistoryChange() {
        console.log('onHistoryChange.')
    }

    render() {
        console.log('render')
        return (
            <div className="container">
                <h3>ValidationFormApp</h3>
                {this.state.message && <div className="alert alert-success">{this.state.message}</div>}
                {
                    this.state.phoneHistory.map(
                        phone => 
                        <p>
                            {phone}
                        </p>
                    )
                }
                <div className="container">
                    <Formik
                        initialValues={{phone: ''}}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="description" component="div" className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Phone</label>
                                        <Field className="form-control" type="text" name="phone" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>History</label>
                                        <Field className="form-control" component="select" name="history" onChange={ e => this.onHistoryChange }>
                                            {
                                                this.state.phoneHistory.map(
                                                    phone => 
                                                        <option value="{phone}">{phone}</option>
                                                )
                                            }
                                        </Field>
                                    </fieldset>

                                    <button className="btn btn-success" type="submit">validate</button>
                                </Form>
                            )
                        }
                    </Formik>
                </div>
            </div>
        )
    }
}

export default ValidationFormApp
