import React, { Component } from 'react';
import PhoneValidationService from '../service/PhoneValidationService';
import TextInput from './TextInput';

class ValidationApp extends Component {
    constructor(props) {
        super(props)

        this.state = {
            phone: {
                value: ''
            },
            phoneHistory: {
                options: [
                    { value: '', display: ''}
                ]
            },
            message: null
        }


        this.onSubmit = this.onSubmit.bind(this)
    }

    changeHandler = event => {
        const name = event.target.name;
        const value = event.target.value;

        console.log(`name: ${name}, value: ${value}`);
    }

    onSubmit(event) {
        event.preventDefault();
        const data = new FormData(event.target);

        console.log(data.get('phone'));
        const phoneValue = data.get('phone');


        PhoneValidationService.validate(phoneValue)
            .then( rtn =>  {
                console.log(rtn)
                this.setState( state => {
                    const history = state.phoneHistory.concat(phoneValue);
                    return {
                        phoneHistory : history,
                        message : rtn.data.message
                    };
                    })

                }
            )
    }


    render() {
        console.log('render')
        return (
            <div className="container">
                <h3>ValidationFormApp</h3>
                {this.state.message && <div className="alert alert-success">{this.state.message}</div>}
                <TextInput name="phone"
                            value={this.state.phone.value}
                            onChange={this.changeHandler}
                            placeHolder="input phone"
                />
            </div>
        )
    }
}

export default ValidationApp
