import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import PhoneValidationService from '../service/PhoneValidationService';

class ValidationFormApp extends Component {
    constructor(props) {
        super(props)

        this.state = {
            phoneHistory: [],
            message: null
        }
    }

    onSubmit(values) {
        let phoneValue = values.phone

        this.setState( state => {
            const history = state.phoneHistory.concat(phoneValue);
            return {
                phoneHistory : history
            };
            })

        PhoneValidationService.validate(phoneValue)
            .then( rtn => console.log(rtn))
    }

    render() {
        console.log('render')
        return (
            <div className="container">
                <h3>ValidationFormApp</h3>
                {this.state.message && <div class="alert alert-success">{this.state.message}</div>}
                {
                    this.state.phoneHistory.map(
                        phone => 
                        <p>
                            {phone}
                        </p>
                    )
                }
                <div className="container">
                    <Formik
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="description" component="div" className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Phone</label>
                                        <Field className="form-control" type="text" name="phone" />
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Check</button>
                                </Form>
                            )
                        }
                    </Formik>
                </div>
            </div>
        )
    }
}

export default ValidationFormApp
